# Given two strings s1 and s2, return true if s2 contains a permutation of s1, or false otherwise.
# In other words, return true if one of s1's permutations is the substring of s2.

# Example 1:

# Input: s1 = "ab", s2 = "eidbaooo"
# Output: true
# Explanation: s2 contains one permutation of s1 ("ba").

# Input: s1 = "ab", s2 = "eidboaoo"
# Output: false

# @param {String} s1
# @param {String} s2
# @return {Boolean}

# USING HASH
def check_inclusion(s1, s2)
  return false if s2.length > 10**4
  left = 0
  right = s1.length-1
  s1_hash = Hash.new(0)
  s2_hash = Hash.new(0)
  

  s1.chars.each do |char|
    s1_hash[char] += 1
  end

  while right <= s2.length-1 do
    left_count = left

    # creatign s2 window hash
    # check for permutation by compare s2 hash with s1 hash
    res_c = 0
    while left_count <= right do
      s2_hash[s2[left_count]] += 1
      if s1_hash.key?(s2[left_count])
        if (s1_hash[s2[left_count]] == s2_hash[s2[left_count]])
          res_c += s1_hash[s2[left_count]]
        end
      end
      return true if res_c == s1.length
      left_count += 1
    end
    
    s2_hash.clear
    left += 1
    right += 1
  end
  false
end

p check_inclusion('ab', 'eidbaooo') # true
p check_inclusion('ab', 'eidboaoo') # false
p check_inclusion('adc', 'dcda') # true
p check_inclusion('hello', 'ooolleoooleh') # false
p check_inclusion('abcdxabcde', 'abcdeabcdx') # true

p check_inclusion(
  "",
  ""
)